calc.equation_logic_text:
var calc_output = [];
var points;
try { nih_stroke_score = parseFloat(nih_stroke_score);
    var agep = parseFloat(age);
    var htn = parseFloat(hypertension);
    var dm = parseFloat(diabetes);
    var afib = parseFloat(atrial_fibrillation);
    var age, nihss, cdsA, cdsB, cdsC, cds, outcome, value;
    if (nih_stroke_score < 11) { nihss = 0 }
    if (nih_stroke_score > 10 && nih_stroke_score < 21) { nihss = 2 }
    if (nih_stroke_score > 20) { nihss = 4 }
    if (agep < 60) { age = 0 }
    if (agep > 59 && agep < 80) { age = 1 }
    if (agep > 79) { age = 2 }
    cds = htn + dm + afib;
    if (cds == 0) { cdsA = 0;
        cdsB = 0;
        cdsC = 0 }
    if (cds == 1) { cdsA = 1;
        cdsB = 0;
        cdsC = 0 }
    if (cds == 2) { cdsA = 0;
        cdsB = 1;
        cdsC = 0 }
    if (cds == 3) { cdsA = 0;
        cdsB = 0;
        cdsC = 1 }
    value = nihss + age + htn + dm + afib;
    outcome = 4.942 + (-0.035 * agep) + (-0.190 * nih_stroke_score) + (-0.105 * cdsA) + (-0.408 * cdsB) + (-0.702 * cdsC);
    outcome = (1 / (1 + Math.exp(-outcome)));
    outcome = (outcome * 100);
    if (outcome < 1) { outcome = Math.round(outcome * 10) / 10 } else { outcome = Math.round(outcome) };

    calc_output.push({ 
        name: 'mini', 
        value: outcome + '%', 
        value_text: '', 
        message: 'Chance of good functional outcome' 
    });
    calc_output.push({ 
        name: '1706_THRIVE Score_result (score)', 
        value: value, 
        value_text: 'points', 
        message: 'THRIVE Score' 
    });
    calc_output.push({ 
        name: '1706_THRIVE Score_result (outcome)', 
        value: outcome, 
        value_text: '%', 
        message: 'Good outcome (Modified Rankin 0-2) at 90 days predicted by THRIVE-c' 
    }); 
} catch (ex) { 
    calc_output.push({ 
        name: 'mini', 
        value: ' ', 
        value_text: ' ', 
        message: ex.toString() 
    }); 
}
