calc.equation_logic_text:
var calc_output = [];
var problems_difficult = parseFloat(problems_difficult);
var v = parseFloat(nervous) + parseFloat(control_worrying) + parseFloat(worrying) + parseFloat(trouble_relaxing) + parseFloat(restless) + parseFloat(irritable) + parseFloat(afraid);
var text = '';
var textAppend = '';
var mini_text = '';
if (v < 5) { text = 'No anxiety disorder.';
    mini_text = 'No anxiety disorder.' }
if (v > 4 && v < 10) { text = 'Mild anxiety disorder.';
    mini_text = 'Mild anxiety disorder.' }
if (v > 9 && v < 15) { text = 'Moderate anxiety disorder.';
    mini_text = 'Moderate anxiety disorder.' }
if (v > 14) { text = 'Severe anxiety disorder.';
    mini_text = 'Severe anxiety disorder.' }
if (v == 8 || v == 9) { textAppend += '<br/><br/>Some studies that applied GAD-7 to Panic Disorder, Social Phobia, and PTSD would use a cutoff of 8, such that this would be Moderate Anxiety. See Next Steps section for more information.'; }
if (v > 0 && problems_difficult == 0) { textAppend += '<br/><br/>Functionally, the patient does not report limitations due to their symptoms.' }
if (v > 0 && problems_difficult == 1) { textAppend += '<br/><br/>Functionally, the patient is \u201csomewhat\u201d having difficulty with life tasks due to their symptoms.' }
if (v > 0 && problems_difficult == 2) { textAppend += '<br/><br/>Functionally, the patient finds it is \u201cvery difficult\u201d to perform life tasks due to their symptoms.' }
if (v > 0 && problems_difficult == 3) { textAppend += '<br/><br/>Functionally, the patient finds it is \u201cextremely difficult\u201d to perform life tasks due to their symptoms.' }
if (v == 0) {textAppend = ' ';}
calc_output.push({ 
    name: 'mini', 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: mini_text 
});
calc_output.push({ 
    name: '1727_GAD-7_result', 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: text + textAppend 
});
