calc.equation_logic_text:
var calc_output=[]; 
var v=parseInt(crp)+parseInt(white_blood_cell_count)+parseInt(hemoglobin)+parseInt(sodium)+parseInt(creatinine)+parseInt(glucose); 
var text='If high suspicion for necrotizing fasciitis through clinical history and physical exam, do not calculate a LRINEC score, and go straight to operative debridement.<br/><br/>'; 
var mini_msg=''; 

if(v<=5){
    text+='Consider IV antibiotics and serial labs to monitor response to treatment. Scores <6 were low risk \u2014 but not no risk \u2014 for necrotizing soft tissue infections.';
    mini_msg='Low Risk, but not no risk.'
} 

if(v==6||v==7){
    text+='Scores of 6 had a PPV of 92% and NPV of 96% for NSTIs. Intermediate Risk for Necrotizing Soft Tissue Infection.<br/><br/>Consider urgent MRI, frozen section biopsy or finger test (manual probing of area in question under local anesthesia). If positive, emergent operative debridement. If negative, IV antibiotics, close observation, serial labs.';
    mini_msg='Intermediate Risk.'
} 

if(v>7){
    text+='High Risk for Necrotizing Soft Tissue Infection. <br/>Scores of 8 had a PPV of 93.4% for NSTIs.<br/>Work-up as above or emergent operative debridement.';
    mini_msg='High Risk.'
} 

calc_output.push({
    name:'mini',
    value:v,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'1734_LRINEC Score_result',
    value:v,
    value_text:'points',
    message:text
});
