calc.equation_logic_text:
var calc_output = [];
var text = '';

//user inputs, addition of selected points
var v = parseFloat(nausea) + parseFloat(tremor) + parseFloat(sweats) + parseFloat(anxiety) + parseFloat(agitation) + parseFloat(tactile) + parseFloat(auditory) + parseFloat(visual_input) + parseFloat(headache) + parseFloat(orientation);

//interpretation
if (v < 9) { 
	text = 'Patients with scores \u22648 typically do not require medication for withdrawal.'; 
}
if (v > 8 && v < 20) { 
	text = 'Patients with scores \u22659 may require medication for withdrawal.'; 
}
if (v > 19) { 
	text = 'Patients with scores \u226520 frequently require medication for withdrawal, and may also require admission to the ICU for observation for seizures or development of delirium tremens, and more frequent medication dosing.'; 
}


calc_output.push({ 
	name: 'mini', 
	value: v.toFixed(0), 
	value_text: 'points', 
	message: 'CIWA-Ar for Alcohol Withdrawal' 
});
calc_output.push({ 
	name: '1736_CIWA-Ar_result', 
	value: v.toFixed(0), 
	value_text: 'points', 
	message: text 
});
