calc.equation_logic_text:
var calc_output = [];
var u = (parseFloat(surgery) + 
	parseFloat(heart_disease) + 
	parseFloat(heart_failure) + 
	parseFloat(cerebrovasclar_disease) + 
	parseFloat(pre_op_insulin) + 
	parseFloat(pre_op_creatinine));

var msg, riskclass;

if (u == 0) {
    msg = '3.9';
    riskclass = 'Class I Risk';
} else if (u == 1) {
    msg = '6.0';
    riskclass = 'Class II Risk';
} else if (u == 2) {
    msg = '10.1';
    riskclass = 'Class III Risk';
} else if (u > 2) {
    msg = '15.0';
    riskclass = 'Class IV Risk';
}

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: riskclass
});
calc_output.push({
    name: '1739_Revised Cardiac Risk Index_result (score)',
    value: u.toFixed(0),
    value_text: 'points',
    message: riskclass
});
calc_output.push({
    name: '1739_Revised Cardiac Risk Index_result (risk)',
    value: msg,
    value_text: '%',
    message: '30-day risk of death, MI, or cardiac arrest<br/><br/>From Duceppe 2017, based on pooled data from 5 high quality external validations (4 prospective). These numbers are higher than those often quoted from the now-outdated original study (Lee 1999). See Evidence for details.'
});
