calc.equation_logic_text:
var calc_output = [];


var msg = '';
var mini_msg = '';
var v = parseInt(history) + parseInt(ekg) + parseInt(age) + parseInt(risk_factors) + parseInt(troponin);
if (v < 4) { msg = 'Low Score (0-3 points)<br/><br/>Risk of MACE of 0.9-1.7%.';
    mini_msg = 'Low Score' }
if (v > 3 && v < 7) { msg = 'Moderate Score (4-6 points)<br/><br/>Risk of MACE of 12-16.6%.';
    mini_msg = 'Moderate Score' }
if (v > 6) { msg = 'High Score (7-10 points)<br/><br/>Risk of MACE of 50-65%.';
    mini_msg = 'High Score' }

if (v<7){
	if (parseInt(ekg) == 2) {
		msg +='<br/><br/>If EKG is highly suspicious, many experts recommend further workup and admission even with a low HEART Score.';
	}
if (parseInt(troponin) == 1 || parseInt(troponin) == 2) {
	msg +='<br/><br/>If troponin is positive, many experts recommend further workup and admission even with a low HEART Score.';
}
}
calc_output.push({ 
	name: 'mini', 
	value: v, 
	value_text: 'points', 
	message: mini_msg 
});
calc_output.push({ 
	name: '1752_HEART Score_result', 
	value: v, 
	value_text: 'points', 
	message: msg 
});
