calc.equation_logic_text:
var calc_output = [];

//user inputs
if(parseFloat(type) == 0){ //typical
	var score = parseFloat(mag) + parseFloat(timingty) + parseFloat(nadir) + parseFloat(thromty) + parseFloat(skin) + parseFloat(acute) + parseFloat(bleed) + parseFloat(pres) + parseFloat(neW) + parseFloat(severe) + parseFloat(dic) + parseFloat(indwell) + parseFloat(cardio) + parseFloat(other);
}

if(parseFloat(type) == 1){	//rapid
	var score = parseFloat(mag) + parseFloat(timingrap) + parseFloat(nadir) + parseFloat(thromrap) + parseFloat(skin) + parseFloat(acute) + parseFloat(bleed) + parseFloat(pres) + parseFloat(neW) + parseFloat(severe) + parseFloat(dic) + parseFloat(indwell) + parseFloat(cardio) + parseFloat(other);
}

if (score > 1) {
    msg = 'Scores \u22652 are 100% sensitive for HIT.';
    mini_msg = 'Sensitive for HIT.';
} else {
    msg = 'Scores <2 suggest a lower probability of HIT.';
    mini_msg = 'Lower probability of HIT.';
}


calc_output.push({ 
	name: 'mini', 
	value: score, 
	value_text: 'points', 
	message: mini_msg 
});
calc_output.push({ 
	name: '1789_HEP Score_result', 
	value: score, 
	value_text: 'points', 
	message: msg 
});
