calc.equation_logic_text:
var calc_output = []; 

var u = parseFloat(sad) + parseFloat(lost_interest) + parseFloat(lacking_energy) + Math.max(parseFloat(self_confident), parseFloat(guilt)) + parseFloat(life) + parseFloat(concentration) + Math.max(parseFloat(restless), parseFloat(subdued)) + parseFloat(sleeping) + Math.max(parseFloat(reduced_appetite), parseFloat(increased_appetite)); 

var msg = ''; if (u < 20) {msg = 'No depression'; } 
else if (u >= 20 && u <= 24) {msg = 'Mild depression'; } 
else if (u >= 25 && u <= 29) {msg = 'Moderate depression'; } 
else if (u >= 30) {msg = 'Severe depression'; } 

calc_output.push({
    name: 'mini', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg 
}); 
calc_output.push({
    name: '1844_Major Depression Index_result', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg 
});
