calc.equation_logic_text:
var calc_output = [];

var timi = parseFloat(age_65) + parseFloat(cad_risk_factors) + parseFloat(known_cad) + parseFloat(aspirin_use) + parseFloat(angina);

var risk;
var interpretation;

if (abnormal_troponin == 1 || abnormal_ekg == 1) {
	risk = 'High'
	interpretation = 'Evaluate through standard chest pain diagnostic pathway.'
}
if (abnormal_troponin == 0 && abnormal_ekg == 0 && timi == 0) {
	risk = 'Low'
	interpretation = '0-0.3% risk of major cardiac event in 30 days.'
}
if (abnormal_troponin == 0 && abnormal_ekg == 0 && timi == 1) {
	risk = 'Intermediate'
	interpretation = '0.8% risk of major cardiac event in 30 days.'
}
if (abnormal_troponin == 0 && abnormal_ekg == 0 && timi > 1) {
	risk = 'Intermediate'
	interpretation = 'At least 0.8% risk of major cardiac event in 30 days (see Facts & Figures for details).'
}

calc_output.push({ 
	name: 'mini', 
	value: risk, 
	value_text: ' risk', 
	message: ''
});
calc_output.push({ 
	name: '1857_ADAPT Protocol_result', 
	value: risk, 
	value_text: 'risk', 
	message: interpretation 
});
