calc.equation_logic_text:
var calc_output=[]; 

var respiratory_rate=parseFloat(respiratory_rate);
var oxygen=parseFloat(oxygen);
var oxygen_supplemental=parseFloat(oxygen_supplemental);
var temperature=parseFloat(temperature);
var systolic_bp=parseFloat(systolic_bp);
var heart_rate=parseFloat(heart_rate);
var avpu_score=parseFloat(avpu_score);

var u=respiratory_rate+oxygen+oxygen_supplemental+temperature+systolic_bp+heart_rate+avpu_score;

var msg=''; 
var mini_msg=''; 

if(respiratory_rate==3||oxygen==3||oxygen_supplemental==3||temperature==3||systolic_bp==3||heart_rate==3||avpu_score==3){
    msg='RED score<br/><br/>Assigned when an extreme variation in a single physiological parameter is present (i.e., a score of 3 in any one physiological parameter.<br/><br/>';
    mini_msg='RED Score '
} 
if(u>=7){
    msg+='High score: Emergency assessment by a clinical/critical care outreach team with critical-care competencies and usually transfer of the patient to a higher dependency care area.';
    mini_msg+='High Score'
} else if(u==5||u==6){
    msg+='Medium score: Urgent review by a clinician skilled with competencies in the assessment of acute illness.';
    mini_msg+='Medium Score'
} else if(u==0||u==1||u==2||u==3||u==4){
    msg+='Low score: Assessment by a competent registered nurse who should decide if a change to frequency of clinical monitoring or an escalation of clinical care is required.';
    mini_msg+='Low Score'
} 

calc_output.push({
    name:'mini',
    value:u.toFixed(0),
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'1873_NEWS Score_result',
    value:u.toFixed(0),
    value_text:'points',
    message:msg
});
