var calc_output = [];
var r, u, x, y, z;
var wt = parseFloat(wt);

var dnr = parseFloat(dnr);
var resp_dist = parseFloat(resp_dist);
var uls = parseFloat(uls);
var ams = parseFloat(ams);
var hr = parseFloat(hr);
var rr = parseFloat(rr);
var sat = parseFloat(sat);
var wt = parseFloat(wt) * 2.2; // weight comes to us in kilos but for this study actually uses lbs

var r = 3.718 + (1.551711 * dnr) + (0.799617 * resp_dist) + (0.734339 * uls) +
  (1.473451 * ams) + (hr * 1.027895) + (0.04422 * rr) + (-0.063644 * sat) + (-
    0.011613 * wt);

var u = 1 + (Math.exp(r));
var x = 1 / u;
var y = 100 * (1 - x);
var z = Math.round(y);

calc_output.push({
  name: 'mini',
  value: z,
  value_text: '%',
  message: 'Chance of 30-day mortality'
});
calc_output.push({
  name: '2022_POMPE-C Tool_result',
  value: z,
  value_text: '%',
  message: 'Chance of mortality within 30 days.'
});
