calc.equation_logic_text:
var calc_output = [];
var v = 0;
v = parseFloat(na) + parseFloat(k) - parseFloat(cl);
var msg = '';
var mini_msg = '';
var msgalt = '';


if (v > 0) {
  msg = 'Urine anion gap positive';
  msgalt =
    'This suggests LOW urinary NH4+ and Renal Tubular Acidosis (RTA) if >10.';
  mini_msg = 'Positive'
}
if (v < 0) {
  msg = 'Urine anion gap negative';
  msgalt =
    'This suggests HIGH urinary NH4+ and possibly diarrheal cause if < -10.';
  mini_msg = 'Negative'
}
if (v == 0) {
  msg = 'Urine anion gap neutral';
  mini_msg = 'Neutral'
}

if (UOMSYSTEM == false) {
  unit = 'mmol/L';
}


if (UOMSYSTEM == true) {
  unit = 'mEq/L';
}

calc_output.push({
  name: 'mini',
  value: v.toFixed(1),
  value_text: unit,
  message: mini_msg + ' Urine Anion Gap'
});
calc_output.push({
  name: '2045_Urine Anion Gap_result',
  value: v.toFixed(1),
  value_text: unit,
  message: msg
});
calc_output.push({
  name: '2045_Urine Anion Gap_interpretation',
  value: ' ',
  value_text: ' ',
  message: msgalt
});
