calc.equation_logic_text:
var calc_output=[]; 
var msg=''; 
var mini_msg=''; 
var u=(parseInt(age)+parseInt(mech)+parseInt(cp)+parseInt(intox)+parseInt(ams)+parseInt(injury)+parseInt(ttp)); 
if (u==0){msg='No thoracic imaging required.<br/><br/>NEXUS is 99% sensitive for clinically significant thoracic injury.'; mini_msg='No thoracic imaging required.'; } 
else {msg ='In well-appearing patient with no evidence of multi-organ injury, consider Chest X-ray (CXR) only without CT.<br/><br/>In ill-appearing patients and/or those who will receive workup for other serious injury, consider chest CT as well.'; mini_msg='Thoracic imaging required.';} 

calc_output.push({
    name:'mini',
    value:u,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'2075_NEXUS Chest Rule_result',
    value:u,
    value_text:'points',
    message:msg
});
