var calc_output = []; 

var v = parseFloat(activity) + parseFloat(pulse) + parseFloat(grimace) + parseFloat(appearance) + parseFloat(respirations); 

var msg = ''; 
if (v >= 7) {
    msg = 'Scores ≥7 are typically \u201cnormal\u201d for neonates.'; 
} else if (v < 7) {
    msg = 'Scores <7 suggest potential need for medical intervention, like suction, drying, warming, and stimulating the neonate. Supplemental oxygen may be indicated as well.'; 
} 

calc_output.push({
    name: 'mini', 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: 'APGAR Score'
}); 
calc_output.push({
    name: '23_APGAR Score_result',
    value: v.toFixed(0), 
    value_text: 'points', 
    message: msg 
});
