calc.equation_logic_text:
var calc_output = [];
var age = parseFloat(age)
var ifg = parseFloat(ifg);
var ast = parseFloat(ast);
var alt = parseFloat(alt);
var platelets = parseFloat(platelets);

var astalt = ast/alt;

if (UOMSYSTEM == true) {
    var alb = parseFloat(alb);
}

if (UOMSYSTEM == false) {
    var alb = parseFloat(alb) / 10;
}

var u, score;
var msg = 'Correlated Fibrosis Severity: ';

u = -1.675 + (0.037 * age) + (0.094 * bmi) + (1.13 * ifg) + (0.99 * astalt) - (0.013 * platelets) - (0.66 * alb)
if (u < -1.455) {
    score = 'F0-F2'
} else if (u >= -1.455 && u <= 0.675) {
    score = 'Indeterminant score';
} else if (u > 0.675) {
    score = 'F3-F4';
}

calc_output.push({
    name: 'mini',
    value: u.toFixed(2),
    value_text: 'points',
    message: score
});
calc_output.push({
    name: '3081_NAFLD Fibrosis Score_result',
    value: u.toFixed(2),
    value_text: 'points',
    message: msg + score
});
