calc.equation_logic_text:
var calc_output = [];
var msg= '';
var mini_msg='';

if (ana == 0) {
  var ana = parseFloat(addtl)
}
else {
  var ana = parseFloat(ana)
}

if (parseFloat(liver_int) + parseFloat(liver_lymph) + parseFloat(liver_ros) == 0) {
  var liver = -5
}
else {
  var liver = parseFloat(liver_int) + parseFloat(liver_lymph) + parseFloat(liver_ros) + parseFloat(liver_bil) + parseFloat(liver_other)
}

var u = parseFloat(sex) + parseFloat(ratio) + parseFloat(serum) + parseFloat(ana) + parseFloat(ama) + parseFloat(hepa) + parseFloat(drug) + parseFloat(alcohol) + parseFloat(liver) + parseFloat(other) + parseFloat(resp);

        if (u < 10) {
            mini_msg='Possible AIH'; msg='<br/><br/>Other testing recommended.';
        } else if (u >= 10 && u <= 12) {
            mini_msg='Pre-treatment: probable AIH.'; msg='<br/><br/>Post-treatment: possible AIH, other testing recommended.';
        } else if (u > 12 && u <= 15) {
            mini_msg='Pre-treatment: probable AIH.'; msg='<br/><br/>Post-treatment: probable AIH';
        } else if (u > 15 && u <= 17) {
            mini_msg='Pre-treatment: definite AIH.'; msg='<br/><br/>Post-treatment: probable AIH.';
        } else if (u > 17) {
            mini_msg='Pre-treatment: definite AIH.'; msg='<br/><br/>Post-treatment: definite AIH.';
        }

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'Revised AIH', value: u, value_text: 'points', message: mini_msg+msg });
