var calc_output = [];

var stool = parseFloat(stool) * 2
var abdp = parseFloat(abdp) * 5 * 7;
var well = parseFloat(well) * 7 * 7;
var abdm = parseFloat(abdm) * 10;
var extra = parseFloat(arth) + parseFloat(irit) + parseFloat(eryth) + parseFloat(anal) + parseFloat(fist) + parseFloat(fev)
var extra = extra * 20
var antid = parseFloat(antid) * 30

var sexVal;
if (parseFloat(sex) == 1) { // male
    sexVal = 47; 
    } 
else { 
    sexVal = 42; // female
    }
var hem = sexVal - parseFloat(hem);
hem = hem * 6

var ibwt = parseFloat(ibw);

var obsWt = parseFloat(wt);
var ratio = (ibwt - obsWt) / ibwt * 100;


var score = stool + abdp + well + extra + antid + abdm + hem + ratio;

var msg, mini_msg;

if (score < 150) {
    mini_msg = 'Remission';
    msg = 'CDAI <150 is used as a marker of remission of Crohnâs disease.';
}
if (score >= 150 && score <= 450) {
    mini_msg = 'Active Crohnâs Disease';
    msg = 'CDAI of 150-450 is used as a marker of active Crohnâs disease. The higher the score, the more severe the disease activity.';
}
if (score > 450) {
    mini_msg = 'Severe Crohnâs disease';
    msg = 'CDAI >450 is used as marker of severe Crohnâs disease.';
}

calc_output.push({
    name: 'mini',
    value: score.toFixed(0),
