calc.equation_logic_text:
var calc_output = [];
var u = parseFloat(resp) + parseFloat(vs) + parseFloat(guard) + parseFloat(activity) + parseFloat(face);
var mini_msg, msg;

if (u <= 2) { mini_msg = "No pain"; msg = ""; }
if (u >= 3 && u <= 6) { mini_msg = "Moderate pain";
    msg = "<br/><br/>Consider analgesia."; }
if (u >= 7 && u <= 10) { mini_msg = "Severe pain";
    msg = "<br/><br/>Consider analgesia."; }

if (u >= 3) { msg += " Sepsis, hypovolemia, and hypoxia need to be resolved prior to interventions."; }


calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'NVPS', value: u, value_text: 'points', message: mini_msg + msg });
