calc.equation_logic_text:
var calc_output = [];
var v = parseFloat(vocal) + parseFloat(face) + parseFloat(body) + parseFloat(physio) + parseFloat(physical);
var text, typetext;
var type = parseFloat(type);


if (v <= 2) {text = 'No';}
if (v >= 3 && v <= 7) {text = 'Mild';}
if (v >= 8 && v <= 13) {text = 'Moderate';}
if (v >= 14) {text = 'Severe';}

if (v >= 3) {
    if (type == 1) {text += ' chronic';}

    if (type == 2) {text += ' acute';}

    if (type == 3) {text += ' acute on chronic';}
} else {
    if (type == 1) {text += ' ';}

    if (type == 2) {text += ' ';}

    if (type == 3) {text += ' ';}
}

calc_output.push({ name: 'mini', value: v, value_text: 'points', message: text + ' pain' });
calc_output.push({ name: 'Abbey Pain Scale', value: v, value_text: 'points', message: text + ' pain' });
