function GailCalculator(userCurrentAge, userMenarcheAge, userFirstLiveBirthAge, userFirstDegreeRel, userHadBiopsy, userNumBiopsy, userHyperPlasia, userRace) {
    var self = this;

    this.BcptConvert = {
        //Note in the question that if the age is unknown, user should type in 0        
        getCurrentAge: function(userCurrentAge) {
            switch (true) {
                case (userCurrentAge > 0 && userCurrentAge < 35):
                    return 34
                case (userCurrentAge <= 0 && userCurrentAge >= 90):
                    return 90
                default:
                    return userCurrentAge
            }
        },
        getMenarcheAge: function(userMenarcheAge) {
            switch (userMenarcheAge) {
                case 0:
                    return 99
                case 1:
                    return 10
                case 2:
                    return 13
                case 3:
                    return 14
            }
        },
        getFirstLiveBirthAge: function(userFirstLiveBirthAge) {
            switch (userFirstLiveBirthAge) {
                case 0:
                    return 0
                case 1:
                    return 15
                case 2:
                    return 22
                case 3:
                    return 27
                case 4:
                    return 31
                case 5:
                    return 99
            }
        },
        getFirstDegRelatives: function(userFirstDegreeRel) {
            switch (userFirstDegreeRel) {
                case 0:
                    return 99
                case 1:
                    return 0
                case 2:
                    return 1
                case 3:
                    return 2
            }
        },
        getEverHadBiopsy: function(userHadBiopsy) {
            switch (userHadBiopsy) {
                case 0:
                    return 99
                case 1:
                    return 0
                case 2:
                    return 1
            }
        },
        getNumberOfBiopsy: function(userNumBiopsy) {
            switch (userNumBiopsy) {
                case 1:
                    return 1
                case 2:
                    return 2
            }
        },
        getHyperPlasia: function(userHyperPlasia) {
            switch (userHyperPlasia) {
                case 0:
                    return 99
                case 1:
                    return 0
                case 2:
                    return 1
            }
        },
        getRace: function(userRace) {
            switch (userRace) {
                case 0:
                    return 1
                case 1:
                    return 2
                case 2:
                    return 3
                case 3:
                    return 7
                case 4:
                    return 8
                case 5:
                    return 9
                case 6:
                    return 10
                case 7:
                    return 11
                case 8:
                    return 12
                case 9:
                    return 1    
                default:
                    return 1
            }
        },
        menarcheAge: function(menarcheAge) {
            switch (true) {
                case (menarcheAge >= 7 && menarcheAge < 12):
                    return 2
                case (menarcheAge >= 12 && menarcheAge < 14):
                    return 1
                case (menarcheAge >= 14 && menarcheAge <= 39 || menarcheAge == 99):
                    return 0
                default:
                    return 0
            }
        },
        firstLiveBirthAge: function(firstLiveBirthAge) {
            switch (true) {
                case (firstLiveBirthAge == 0):
                    return 2
                case (firstLiveBirthAge < 20 || firstLiveBirthAge == 99):
                    return 0
                case (firstLiveBirthAge >= 20 && firstLiveBirthAge < 25):
                    return 1
                case (firstLiveBirthAge >= 25 && firstLiveBirthAge < 30):
                    return 2
                case (firstLiveBirthAge >= 30 && firstLiveBirthAge <= 55):
                    return 3
                default:
                    return 0
            }
        },
        everHadBiopsy: function(everHadBiopsy) {
            switch (everHadBiopsy) {
                case (99):
                    return 0
                default:
                    return everHadBiopsy
            }
        },
        numberOfBiopsy: function(numberOfPreviousBiopsy, everHadBiopsy) {
            switch (true) {
                case (everHadBiopsy == 99):
                    return 99
                case (numberOfPreviousBiopsy == 0 || (numberOfPreviousBiopsy == 99 && everHadBiopsy == 99)):
                    return 0
                case (numberOfPreviousBiopsy == 1 || (numberOfPreviousBiopsy == 99 && everHadBiopsy == 1)):
                    return 1
                case (numberOfPreviousBiopsy > 1 && numberOfPreviousBiopsy <= 30):
                    return 2
                default:
                    return 0
            }
        },
        hyperplasia: function(hyperPlasia, everHadBiopsy) {
            switch (true) {
                case (everHadBiopsy == 0):
                    return 99
                default:
                    return hyperPlasia
            }
        },
        firstDegRelatives: function(firstDegRelatives, race) {
            if (race == undefined) {
                switch (true) {
                    case (firstDegRelatives == 0 || firstDegRelatives == 99):
                        return 0
                    case (firstDegRelatives == 1):
                        return 1
                    case (firstDegRelatives >= 2 && firstDegRelatives <= 31):
                        return 2
                    default:
                        return 0
                }
            } else {
                switch (true) {
                    case (firstDegRelatives == 0 || firstDegRelatives == 99):
                        return 0
                    case (firstDegRelatives == 1):
                        return 1
                    case (firstDegRelatives >= 2 && firstDegRelatives <= 31 && race < 7):
                        return 2
                    case (firstDegRelatives >= 2 && race >= 7):
                        return 1
                    default:
                        return 0
                }
            }

        },
        currentAgeIndicator: function(currentAge) {
            switch (true) {
                case (currentAge < 50):
                    return 0
                case (currentAge >= 50):
                    return 1
                default:
                    return 0
            }
        },
