    this.RiskCalculator = {
        calculateRisk: function(riskindex, currentAge, projectionAge, ageIndicator, numberOfBiopsy, menarcheAge, firstLiveBirthAge, firstDegRelatives, everHadBiopsy, ihyp, rhyp, irace) {

            var numCovPattInGailModel = 216
            var bet2 = self.InitiateRickCalculatorVars.bet2(8, 12)
            var t = self.InitiateRickCalculatorVars.t(15)
            var rmu2 = self.InitiateRickCalculatorVars.rmu2(14, 12)
            var rlan2 = self.InitiateRickCalculatorVars.rlan2(14, 12)
            var rf2 = self.InitiateRickCalculatorVars.rf2(2, 13)

            var bet = []
            var rf = []
            var abs = []
            var rlan = []
            var rmu = []
            var sumb = []
            var sumbb = []

            var retval = 0.0;
            var i
            var j
            var k
            var n = 216
            var r = 0.0
            var ni = 0
            var ti = currentAge
            var ns = 0
            var ts = projectionAge
            var abss = 0.0
            var incr
            var ilev
            var r8iTox2 = self.Conveniences.createTwoDimensionArray(216, 9)

            for (i = 0; i < 8; i++) {
                bet[i] = bet2[i][irace - 1];
            }


            if (irace == 2) {
                if (menarcheAge == 2) {
                    menarcheAge = 1;
                    firstLiveBirthAge = 0;
                }
            }

            for (i = 1; i <= 15; ++i) {
                if (ti < t[i - 1]) {
                    ni = i - 1;
                    break;
                }
            }

            for (i = 1; i <= 15; ++i) {
                if (ts <= t[i - 1]) {
                    ns = i - 1
                    break;
                }
            }

            incr = 0;

            if (riskindex == 2 && irace < 7) {
                incr = 3;
            }

            cindx = 0;
            cindx = incr + irace - 1;

            for (i = 0; i < 14; ++i) {
                rmu[i] = rmu2[i][cindx];
                rlan[i] = rlan2[i][cindx];
            }

            rf[0] = rf2[0][incr + irace - 1];
            rf[1] = rf2[1][incr + irace - 1];

            if (riskindex == 2 && irace >= 7) {
                rf[0] = rf2[0][12];
                rf[1] = rf2[1][12];
            }

            if (riskindex >= 2) {
                menarcheAge = 0;
                numberOfBiopsy = 0;
                firstLiveBirthAge = 0;
                firstDegRelatives = 0;
                rhyp = 1.0;
            }

            ilev = ageIndicator * 108 + menarcheAge * 36 + numberOfBiopsy * 12 + firstLiveBirthAge * 3 + firstDegRelatives + 1;

            for (k = 0; k < 216; ++k) {
                r8iTox2[k][0] = 1.0;
            }

            for (k = 0; k < 108; ++k) {
                r8iTox2[k][1] = 0.0;
                r8iTox2[108 + k][1] = 1.0;
            }

            for (j = 1; j <= 2; ++j) {
                for (k = 1; k <= 36; ++k) {
                    r8iTox2[(j - 1) * 108 + k - 1][2] = 0.0;
                    r8iTox2[(j - 1) * 108 + 36 + k - 1][2] = 1.0;
                    r8iTox2[(j - 1) * 108 + 72 + k - 1][2] = 2.0;
                }
            }

            for (j = 1; j <= 6; ++j) {
                for (k = 1; k <= 12; ++k) {
                    r8iTox2[(j - 1) * 36 + k - 1][3] = 0.0;
                    r8iTox2[(j - 1) * 36 + 12 + k - 1][3] = 1.0;
                    r8iTox2[(j - 1) * 36 + 24 + k - 1][3] = 2.0;
                }
            }

            for (j = 1; j <= 18; ++j) {
                for (k = 1; k <= 3; ++k) {
                    r8iTox2[(j - 1) * 12 + k - 1][4] = 0.0;
                    r8iTox2[(j - 1) * 12 + 3 + k - 1][4] = 1.0;
                    r8iTox2[(j - 1) * 12 + 6 + k - 1][4] = 2.0;
                    r8iTox2[(j - 1) * 12 + 9 + k - 1][4] = 3.0;
                }
            }

            for (j = 1; j <= 72; ++j) {
                r8iTox2[(j - 1) * 3 + 1 - 1][5] = 0.0;
                r8iTox2[(j - 1) * 3 + 2 - 1][5] = 1.0;
                r8iTox2[(j - 1) * 3 + 3 - 1][5] = 2.0;
            }

            for (i = 0; i < 216; ++i) {
                r8iTox2[i][6] = r8iTox2[i][1] * r8iTox2[i][3];
                r8iTox2[i][7] = r8iTox2[i][4] * r8iTox2[i][5];
            }

            for (i = 0; i < 216; ++i) {
                r8iTox2[i][8] = 1.0;
            }

            for (i = 0; i < 216; ++i) {
                sumb[i] = 0.0;
                for (j = 0; j < 8; ++j) {
                    sumb[i] += bet[j] * r8iTox2[i][j];
                }
            }

            for (i = 1; i <= 108; ++i) {
                sumbb[i - 1] = sumb[i - 1] - bet[0];
            }

            for (i = 109; i <= 216; ++i) {
                sumbb[i - 1] = sumb[i - 1] - bet[0] - bet[1];
            }

            for (j = 1; j <= 6; ++j) {
                rlan[j - 1] = rlan[j - 1] * rf[0];
            }

            for (j = 7; j <= 14; ++j) {
                rlan[j - 1] = rlan[j - 1] * rf[1];
            }

            i = ilev

