calc.equation_logic_text:
var calc_output = [];
var L = parseFloat(L);
var A = parseFloat(A);
var C = parseFloat(C);
var E = parseFloat(E);

var u = L + A + C + E;
var risk;
if (u < 5) {
    risk = 'Low';
}
if (u >= 5 && u <= 9) {
    risk = 'Moderate';
}
if (u > 9) {
    risk = 'High';
}

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: risk + ' risk of readmission.'
});
calc_output.push({
    name: 'LACE Index',
    value: u.toFixed(0),
    value_text: 'points',
    message: risk + ' risk of readmission.'
});
