calc.equation_logic_text:
var calc_output = [];

if(parseFloat(entry) === 0){
    calc_output.push({
        name: 'mini',
        value: 'Ineligible',
        value_text: '',
        message: 'This patient is not eligible for scoring. Use only in patients with suspected FH, either by elevated cholesterol level or family history.'
    });
    calc_output.push({
        name: '3818_Dutch Criteria_result',
        value: 'Ineligible',
        value_text: '',
        message: 'This patient is not eligible for scoring. Use only in patients with suspected FH, either by elevated cholesterol level or family history.'
    });

}else{
    var u = Math.max(parseFloat(relative_corvasc), parseFloat(relative_ldl95)) + Math.max(parseFloat(relative_xan), parseFloat(child_ldl95)) + parseFloat(premcad) + parseFloat(premvasc) + parseFloat(xan) + parseFloat(arcus) + parseFloat(ldl) + parseFloat(dna);

    var string1;

    var pt = ' points';
    if (u > 8) {
        string1 = "Definite FH";
    }
    if (u >= 6 && u <= 8) {
        string1 = "Probable FH";
    }
    if (u >= 3 && u <= 5) {
        string1 = "Possible FH";
    }
    if (u < 3) {
        string1 = "Unlikely FH";
    }

    if(u === 1){
        pt = ' point';
    }

    calc_output.push({
        name: 'mini',
        value: string1,
        value_text: '',
        message: u + pt
    });
    calc_output.push({
        name: '3818_Dutch Criteria_result',
        value: string1,
        value_text: '',
        message: u + pt
    });
}
