calc.equation_logic_text:
var calc_output = [];
var micro = parseFloat(micro);
var alb =  parseFloat(alb);

var string1, string2; 
var mini_msg = 'Multiple Myeloma';

if (micro == 1 && alb == 2 ) {string1 = 'Stage I';string2='Median survival is 62 months';}

if (micro == 1 && alb == 1 ) {string1 = 'Stage II'; string2='Median survival is 44 months';}

if (micro == 2 ) {string1 = 'Stage II'; string2='Median survival is 44 months';}

if (micro == 3 ) {string1 = 'Stage III'; string2='Median survival is 29 months';}


calc_output.push({
    name: 'mini',
    value: string1,
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: 'mISS',
    value: string1,
    value_text: ' ',
    message: mini_msg +'<br/><br/>'+ string2
});
