var calc_output = [];

var u = parseFloat(ichvol) + parseFloat(age) + parseFloat(location) + parseFloat(gcs) + parseFloat(cog);

var msg;

if (u <= 4) {
    msg = 'Entire cohort: 0% probability of functional independence at 90 days<br/>Survivors only: 0% probability of functional independence at 90 days';
} else if (u <= 7) {
    msg = 'Entire cohort: 13% probability of functional independence at 90 days<br/>Survivors only: 29% probability of functional independence at 90 days';
} else if (u == 8) {
    msg = 'Entire cohort: 42% probability of functional independence at 90 days<br/>Survivors only: 48% probability of functional independence at 90 days';
} else if (u <= 10) {
    msg = 'Entire cohort: 66% probability of functional independence at 90 days<br/>Survivors only: 75% probability of functional independence at 90 days';
} else {
    msg = 'Entire cohort: 82% probability of functional independence at 90 days<br/>Survivors only: 95% probability of functional independence at 90 days';
}

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: 'FUNC Score',
    value: u,
    value_text: 'points',
    message: msg
});
