calc.equation_logic_text:
var calc_output = [];

var sbp = parseFloat(sbp);
var cxr = parseFloat(cxr);
var albumin = parseFloat(albumin);
var hr = parseFloat(hr);
var confusion = parseFloat(confusion);
var ph = parseFloat(ph);


var age = parseFloat(age);
var u, pri_u;

if (age == "0") {
    // var rr25 = parseFloat(rr25) ? 1 : 0;
   // var oxygen70 = parseFloat(oxygen70) ? 1 : 0;
    var rr25 = parseFloat(rr25);
    var oxygen70 = parseFloat(oxygen70);
    u = sbp + cxr + albumin + hr + confusion + ph + rr25 + oxygen70; 
    pri_u = sbp + cxr + hr + confusion + rr25;   
}
if (age == "1") {
    // var rr30 = parseFloat(rr30) ? 1 : 0;
    // var oxygen60 = parseFloat(oxygen60) ? 1 : 0;
    var rr30 = parseFloat(rr30);
    var oxygen60 = parseFloat(oxygen60);
    u = sbp + cxr + albumin + hr + confusion + ph + rr30 + oxygen60;
    pri_u = sbp + cxr + hr + confusion + rr30; 
}

//interpretation
var msg, risk;
var add = '';
if (u <= 2) {
    msg = 'Low';
    risk = 'Minimal';
}else if (u == 3 || u == 4) {
    msg = 'Moderate';
    risk = '1 in 8';
}else if (u == 5 || u == 6) {
    msg = 'High';
    add = 'Consider ICU admission.';
    risk = '1 in 3';
}else { //if (u >= 7) 
    msg = 'Very high';
    add = 'Consider ICU admission.';
    risk = '2 in 3';
}

//primary care interpretation
var pri_msg, pri_risk;
if (pri_u == 0) {
    pri_msg = 'Very low';
    pri_risk = 'Minimal';
}else if (pri_u == 1) {
    pri_msg = 'Low';
    pri_risk = '1 in 20';
}else if (pri_u == 2) {
    pri_msg = 'Moderate';
    pri_risk = '1 in 10';
}else if (pri_u == 3) {
    pri_msg = 'High';
    pri_risk = '1 in 6';
}else {  //if (u >= 4) 
    pri_msg = 'High';
    pri_risk = '1 in 3';
}

calc_output.push({ 
    name: 'mini',
    value: u, 
    value_text: 'points', 
    message: msg + ' risk' 
});
calc_output.push({ 
    name: 'SMART-COP', 
    value: u, 
    value_text: 'points', 
    message: 'SMART-COP Score'
});
calc_output.push({ 
    name: 'Risk', 
    value: msg, 
    value_text: 'risk ', 
    message: risk + ' risk of IRVS. ' + add
});
