var calc_output = [];

var u = parseFloat(age) + parseFloat(aids) + parseFloat(chf) + parseFloat(connective_tissue_disease) + parseFloat(copd) + parseFloat(cva_or_tia) + parseFloat(dementia) + parseFloat(dm) + parseFloat(hemiplegia) + parseFloat(liver) + parseFloat(ckd) + parseFloat(mi) + parseFloat(pud) + parseFloat(pvd) + parseFloat(lymph) + parseFloat(leuk) + parseFloat(solid);

var Y = Math.exp(parseFloat(u) * 0.9);
var tenYearSurvival = Math.pow(0.983, Y);
tenYearSurvival = tenYearSurvival.toFixed(2) * 100;

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: tenYearSurvival + '% 10-year survival'
});
calc_output.push({
    name: '3917_Charlson Comorbidity Index_result (score)',
    value: u,
    value_text: 'points',
    message: 'Charlson Comorbidity Index'
});
calc_output.push({
    name: '3917_Charlson Comorbidity Index_result (survival)',
    value: tenYearSurvival,
    value_text: '%',
    message: 'Estimated 10-year survival'
});
