calc.equation_logic_text:
var calc_output = [];

var u = parseFloat(unstable) + parseFloat(thrombolysis) + parseFloat(activebleeding) + parseFloat(oxygen) + parseFloat(anticoagulant) + parseFloat(painmeds) + parseFloat(medicalsocial) + parseFloat(crcl) + parseFloat(liver) + parseFloat(preg) + parseFloat(hit);
var msg, mini_msg, risk;
if (u < 1) { mini_msg = "Outpatient treatment";
    msg = "Patient eligible for outpatient treatment (0% mortality, 2% VTE recurrence).";
    risk = 'Low'
} else { mini_msg = "Inpatient treatment";
    msg = "Patient <u>not</u> eligible for outpatient treatment per the Hestia Criteria, which recommends admission and inpatient treatment.";
    risk = 'Not low'
}

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: 'hestia',
    value: u,
    value_text: 'points',
    message: 'Hestia Criteria'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: msg
});
