calc.equation_logic_text:
var calc_output=[];

var sx = parseInt(sx);
var ex =  parseInt(ex);
var fev =  parseInt(fev);

var group, risk, mort, tx, grade;

if (sx == 0) {
	if (ex == 0 || ex == 1) {
	group = "GOLD A";
	risk = "Low risk, low symptom burden"; 
	mort = "3.8";
	tx = "Bronchodilator (long- or short-acting) recommended";
	}
	else {
		group = "GOLD C";
		risk = "High risk, low symptom burden";
		mort = "8.2";
		tx = "LAMA (or LAMA + LABA vs. LABA + ICS if frequent exacerbations) recommended";
	}
}
if (sx == 1) {
	if (ex == 0 || ex == 1) {
	group = "GOLD B";
	risk = "Low risk, higher symptom burden"; 
	mort = "10.6";
	tx = "LABA or LAMA recommended";
	}
	else {
		group = "GOLD D";
		risk = "High risk, higher symptom burden";
		mort = "20.1";
		tx = "LAMA + LABA recommended, plus ICS if frequent exacerbations";
	}
}

if (fev == 0) {
	grade = 'Grade 1'
}
if (fev == 1) {
	grade = 'Grade 2'
}
if (fev == 2) {
	grade = 'Grade 3'
}
if (fev == 3) {
	grade = 'Grade 4'
}

calc_output.push({
    name: 'mini',
    value: group,
    value_text: '',
    message: mort + '% mortality'
});
calc_output.push({
    name: 'GOLD Criteria',
    value: group,
    value_text: '',
    message: grade + ' airflow obstruction'
});
calc_output.push({
    name: 'risk',
    value: mort,
    value_text: '%',
    message: '3-year mortality<br/><br/> ' + tx
});
