calc.equation_logic_text:
var calc_output = [];

//user inputs
var clas = parseFloat(clas);

var app, re, mort;
if(clas === 1){
	app = '1A';
	re = '55';
	mort = '11';
}else if(clas === 11){
	app = '1B';
	re = '55';
	mort = '11';
}else if(clas === 2){
	app = '2A';
	re = '43';
	mort = '11';
}else if(clas === 22){
	app = '2B';
	re = '22';
	mort = '7';
}else if(clas === 222){
	app = '2C';
	re = '10';
	mort = '3';
}else if(clas === 3){
	app = '3';
	re = '5';
	mort = '2';
}

//results
calc_output.push({
    name: 'mini',
    value: app,
    value_text: 'Forrest',
    message: mort + '% mortality'
});
calc_output.push({
    name: 'Class',
    value: app,
    value_text: '',
    message: 'Forrest classification'
});
calc_output.push({
    name: 'Rebleeding',
    value: re,
    value_text: '%',
    message: 'Rebleeding rate'
});
calc_output.push({
    name: 'Mortality',
    value: mort,
    value_text: '%',
    message: 'Mortality'
});
