calc.equation_logic_text:
var calc_output = [];

var u = parseFloat(rlq) + parseFloat(anorexia) + parseFloat(fever) + parseFloat(nausea) + parseFloat(rif) + parseFloat(wbc) + parseFloat(shift) + parseFloat(migration);
var risk;
var msg;

// define risk
if (u < 4 ) {
	risk = 'Low risk according to PAS'; 
	msg = 'Unlikely appendicitis. Consider other diagnoses.'
}

if (u == 4 || u == 5 || u == 6) {
	risk = 'Equivocal risk according to PAS'; 
	msg = 'Cannot definitively rule in or rule out appendicitis. Consider imaging and/or surgical consult.'
}

if (u > 6) {
	risk = 'High risk according to PAS'; 
	msg = 'Likely appendicitis. Consider surgical consult.'
}

// result on app
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: msg
});

// result on web
calc_output.push({
    name: 'pas',
    value: u,
    value_text: 'points',
    message: msg
});
