calc.equation_logic_text:
var calc_output = [];
var age = parseFloat(age);
var sbp = parseFloat(sbp);
var pulse = parseFloat(pulse);
var gcs = parseFloat(gcs);
if (UOMSYSTEM == false) {
       var hb = parseFloat(hb) / 10;
     }

   if (UOMSYSTEM == true) {
       var hb = parseFloat(hb);
}
var wbc = parseFloat(wbc);
if (UOMSYSTEM == false) {
       var bun = parseFloat(bun) / 0.357;
     }

   if (UOMSYSTEM == true) {
       var bun = parseFloat(bun);
}
var na = parseFloat(na);
var k = parseFloat(k);
var bloodloss = parseFloat(bloodloss);

var agescore, sbpscore, pulsescore, gcsscore, hbscore, wbcscore, bunscore, nascore, kscore, bloodlossscore;

if (age < 61) {
    agescore = 1;
}
if (age >= 61 && age < 71) {
    agescore = 2;
}
if (age >= 71) {
    agescore = 4;
}

if (sbp < 90) {
    sbpscore = 8;
}
if (sbp >= 90 && sbp < 100) {
    sbpscore = 4;
}
if (sbp >= 100 && sbp < 110) {
    sbpscore = 2;
}
if (sbp >= 110 && sbp <= 130) {
    sbpscore = 1;
}
if (sbp > 130 && sbp <= 170) {
    sbpscore = 2;
}
if (sbp > 170) {
    sbpscore = 4;
}

if (pulse < 40) {
    pulsescore = 8;
}
if (pulse >= 40 && pulse < 50) {
    pulsescore = 2;
}
if (pulse >= 50 && pulse < 81) {
    pulsescore = 1;
}
if (pulse >= 81 && pulse < 101) {
    pulsescore = 2;
}
if (pulse >= 101 && pulse <= 120) {
    pulsescore = 4;
}
if (pulse > 120) {
    pulsescore = 8;
}


if (gcs <= 8) {
    gcsscore = 8;
}
if (gcs >= 9 && gcs <= 11) {
    gcsscore = 4;
}
if (gcs >= 12 && gcs <= 14) {
    gcsscore = 2;
}
if (gcs == 15) {
    gcsscore = 1;
}


if (hb < 10) {
    hbscore = 8;
}
if (hb >= 10.0 && hb < 11.5) {
    hbscore = 4;
}
if (hb >= 11.5 && hb < 13) {
    hbscore = 2;
}
if (hb >= 13 && hb <= 16) {
    hbscore = 1;
}
if (hb > 16 && hb <= 17.0) {
    hbscore = 2;
}
if (hb > 17 && hb <= 18.0) {
    hbscore = 4;
}
if (hb > 18) {
    hbscore = 8;
}


if (wbc < 3.1) {
    wbcscore = 4;
}
if (wbc >= 3.1 && wbc < 4) {
    wbcscore = 2;
}
if (wbc >= 4 && wbc <= 10) {
    wbcscore = 1;
}
if (wbc > 10 && wbc <= 20) {
    wbcscore = 2;
}
if (wbc > 20) {
    wbcscore = 4;
}


if (bun < 21) {
    bunscore = 1;
}
if (bun >= 21 && bun < 28) {
    bunscore = 2;
}
if (bun >= 28 && bun <= 42) {
    bunscore = 4;
}
if (bun > 42) {
    bunscore = 8;
}

if (na < 125) {
    nascore = 8;
}
if (na >= 125 && na < 131) {
    nascore = 4;
}
if (na >= 131 && na <= 136) {
    nascore = 2;
}
if (na > 136) {
    nascore = 1;
}

if (k < 2.9) {
    kscore = 8;
}
if (k >= 2.9 && k < 3.2) {
    kscore = 4;
}
if (k >= 3.2 && k < 3.5) {
    kscore = 2;
}
if (k >= 3.5 && k <= 5.0) {
    kscore = 1;
}
if (k > 5.0 && k <= 5.3) {
    kscore = 4;
}
if (k > 5.3 && k < 6.0) {
    kscore = 4;
}
if (k >= 6.0) {
    kscore = 8;
}

if (bloodloss <= 100) {
    bloodlossscore = 1;
}
if (bloodloss > 100 && bloodloss <= 500) {
    bloodlossscore = 2;
}
if (bloodloss > 500 && bloodloss < 1000) {
    bloodlossscore = 4;
}
if (bloodloss >= 1000) {
    bloodlossscore = 8;
}


var phys = agescore + sbpscore + pulsescore + gcsscore + hbscore + wbcscore + bunscore + nascore + kscore + parseFloat(card) + parseFloat(resp) + parseFloat(ekg);
var op = parseFloat(opsev) + parseFloat(proc) + bloodlossscore + parseFloat(soil) + parseFloat(malig) + parseFloat(mode);


var morb = 1 / (1 + (1 / Math.exp(-5.91 + (0.16 * phys) + (0.19 * op))));
var mort = 1 / (1 + (1 / Math.exp(-7.04 + (0.13 * phys) + (0.16 * op))));
var morbval = morb * 100;
var mortval = mort * 100;

calc_output.push({
    name: 'mini',
    value: mortval.toFixed(1),
    value_text: '%',
    message: 'Predicted mortality'
});
calc_output.push({
    name: 'POSSUM mort',
    value: mortval.toFixed(1),
    value_text: '%',
    message: 'Predicted mortality'
});
calc_output.push({
    name: 'POSSUM morb',
    value: morbval.toFixed(1),
    value_text: '%',
    message: 'Predicted morbidity'
});
calc_output.push({
    name: 'POSSUM score',
    value: ' ',
    value_text: ' ',
    message: 'Physiological score: ' + phys + ' points<br/><br/>Operative severity score: ' + op + ' points'
});
