calc.equation_logic_text:
var calc_output = [];

//user inputs
var age = parseFloat(age);
var nursing = parseFloat(nursing);
var prioriv = parseFloat(prioriv);
var hospital = parseFloat(hospital);
var icu = parseFloat(icu);
var cereb = parseFloat(cereb);
var dementia = parseFloat(dementia);
var fdiabetes = parseFloat(fdiabetes);

//add points up
var u = age + nursing + prioriv + hospital + icu + cereb + dementia + fdiabetes;

//results
var risk, minirisk, msg;
if (u < 2){
	risk = 'Low';
    minirisk = 'Low';
	msg = 'Consider routine antibiotic coverage.';
}else if(1 < u < 6){
	risk = 'Intermediate';
    minirisk = 'Medium';
	msg = 'Use clinical judgment regarding antibiotic coverage.';
}
if(u > 5){
	risk = 'High';
    minirisk = 'High';
	msg = 'Consider extended antibiotic coverage.';
}

//results
calc_output.push({
    name: 'mini',
    value: minirisk,
    value_text: '',
    message: 'Risk for MRSA'
});
calc_output.push({
    name: 'Score',
    value: u,
    value_text: 'points',
    message: 'Shorr Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: msg
});
