calc.equation_logic_text:
var calc_output = [];

//user inputs
var type = parseFloat(type);
var classd = parseFloat(classd);
var age = parseFloat(age);
var disability = parseFloat(disability);
var nih = parseFloat(nih);

var modu = type + classd + disability + age + nih;

//interpretation   [inpatient mortality rate SOAR score, inpatient mortality rate mSOAR score]

var mortality = [ 1.0, 1.0, 1.5, 6.5, 9.2, 19.5, 26.2, 49.2 ]

var inpatMS = mortality [modu]

if (modu > 7) {
    inpatMS = '>49.2'
    msg = '. Note: scores >7 were not reported.'
}
else {
    inpatMS = inpatMS
    msg = ''
}


//results
calc_output.push({
    name: 'mini',
    value: modu,
    value_text: 'points',
    message: inpatMS + '% inpatient mortality'
});
calc_output.push({
    name: 'Mod Score',
    value: modu,
    value_text: 'points',
    message: 'mSOAR Score'
});
calc_output.push({
    name: 'Mod Score',
    value: inpatMS,
    value_text: '%',
    message: 'Inpatient mortality' + msg
});
