calc.equation_logic_text:
var calc_output = [];

//user inputs - addition of selected points
var score = parseFloat(arr)+parseFloat(cardiac)+parseFloat(ibd)+parseFloat(diabetes)+parseFloat(cd)+parseFloat(psych)+parseFloat(hepatic)+parseFloat(obesity)+parseFloat(infection)+parseFloat(rheu)+parseFloat(peptic)+parseFloat(renal)+parseFloat(pulm)+parseFloat(prior);

//interpretation
var mort1, mort3, survival1, survival3, intensity, aamort, aasurvival;
if(trans==0){
    if(score == 0){
    	mort1 = '17';
        mort3 = '24%';
    	survival1 = '69%';
        survival3 = '54%';
    }else if(0 < score < 3){
        mort1 = '21'; 
        mort3 = '28%';
        survival1 = '62%';
        survival3 = '47%';
    }
    if (score > 2){
        mort1 = '26'; 
        mort3 = '35%';
        survival1 = '56%';
        survival3 = '38%';
    }
}
if(trans==1){
    if(score == 0){
        mort1 = '3';
        mort3 = '5%';
        survival1 = '91%';
        survival3 = '79%';
    }else if(0 < score < 3){
        mort1 = '3';
        mort3 = '6%';
        survival1 = '88%';
        survival3 = '73%';
    }
    if (score > 2){
        mort1 = '5';
        mort3 = '9%';
        survival1 = '86%';
        survival3 = '70%';
    } 
}

var age = parseFloat(age);
var aascore = score;
if(age === 1 || age === 0){
    aascore = score + age;  //age adjusted score
    var pt = 'points';
    if(aascore === 1){
        pt = 'point';
    }
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: mort1 + '% 1-yr NRM'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'HCT-CI Score'
});
calc_output.push({
    name: 'non-relapse mortality',
    value: mort1,
    value_text: '%',
    message: 'NRM at 1 year<br/><br/>' + '1-year OS: ' + survival1 + '<br/>3-year NRM: ' + mort3 + '<br/>3-year OS: ' + survival3 
});
if(age == 1 || age == 0){
    if(intensity==0){ // NMA
        if(aascore === 0){
            aamort = '5%';
            aasurvival = '81%';
        }else if(aascore === 1 || aascore === 2){
            aamort = '9%';
            aasurvival = '74%';
        }else if(aascore === 3 || aascore === 4){
            aamort = '17%';
            aasurvival = '59%';
        }else if(aascore > 4){
            aamort = '35%';
            aasurvival = '37%';
        }
    }
    if(intensity==1){ // RIC
        if(aascore === 0){
            aamort = '12%';
            aasurvival = '87%';
        }else if(aascore === 1 || aascore === 2){
            aamort = '18%';
            aasurvival = '70%';
        }else if(aascore === 3 || aascore === 4){
            aamort = '36%';
            aasurvival = '50%';
        }else if(aascore > 4){
            aamort = '41%';
            aasurvival = '35%';
        }
    }
    if(intensity==2){ // MAC
        if(aascore === 0){
            aamort = '10%';
            aasurvival = '79%';
        }else if(aascore === 1 || aascore === 2){
            aamort = '20%';
            aasurvival = '66%';
        }else if(aascore === 3 || aascore === 4){
            aamort = '37%';
            aasurvival = '45%';
        }else if(aascore > 4){
            aamort = '49%';
            aasurvival = '29%';
        }
    }

    calc_output.push({
    name: 'aaScore',
    value: aascore,
    value_text: pt,
    message: 'Age-adjusted HCT-CI Score (allo-HCT only)<br/><br/>2-year NRM: ' + aamort + '<br/>2-year OS: ' + aasurvival
});
}
