calc.equation_logic_text:
var calc_output = [];

//user inputs 
var cxr = (parseFloat(cxr) === -1) ? 7 : parseFloat(cxr);
var ratio = (parseFloat(ratio) === -1) ? 7 : parseFloat(ratio);
var peep = (parseFloat(peep) === -1) ? 7 : parseFloat(peep);
var comp = (parseFloat(comp) === -1) ? 7 : parseFloat(comp);
var array = [cxr, ratio, peep, comp];

var count = 0;
var score = 0;
for(var x in array){
	var element = array[x];
	if(element !== 5){
		score = score + element;
		count++;
	}
}

var msg, eligible;

var final = score / count;

final = final.toFixed(1);

if (count == 0){
  	eligible = 'Eligibility cannot be assessed';
}
else if(final == 0 && (cxr==5 || ratio==5 || peep==5 || comp==5)){
    msg = 'No lung injury by Murray Score.';
	eligible = '';
}
else if(final == 0){
    msg = 'No lung injury. ';
	eligible = 'Not eligible for ECMO.';
}
else if(final > 0 && final <= 2.5){
    msg = 'Mild to moderate lung injury. ';
    eligible = 'Not eligible for ECMO.';
}
else if(final > 2.5 && final < 3.0){
    msg = 'Severe lung injury. ';
    eligible = 'Not eligible for ECMO.';
}
else if(final >= 3.0){
	eligible = 'Consider referral to ECMO center.';
    msg = 'Severe lung injury. ';
}

    calc_output.push({
    name: 'mini',
    value: final,
    value_text: 'points',
    message: msg
    });
if (count == 0){
   calc_output.push({
       name: 'Result',
       value: '',
       value_text: '',
       message: eligible
    });}
else{
    calc_output.push({
       name: 'Result',
       value: final,
       value_text: 'points',
       message: msg + eligible
    });}
