var calc_output = [];

//user inputs - addition of selected points
var score = parseFloat(ldh) + parseFloat(ecog) + parseFloat(serum) + parseFloat(tumor);

//interpretation
var risk, survival;
if(score < 2){
	risk = 'Low';
	survival = '319';
}else if(score < 5){
	risk = 'Moderate';
	survival = '130';
}else{
	risk = 'High';
	survival = '44';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'LENT Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'LENT Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: survival + ' days median survival'
});
