var calc_output = [];

//user inputs
var initial = parseFloat(infant) + parseFloat(asx) + parseFloat(hp) + parseFloat(episode);

var msg, risk, rec;

if (initial < 4){
    msg = "Not BRUE"
    risk = ""
    rec = "Entry criteria not fulfilled"
}
if(initial == 4){
    var brue = parseFloat(cyanosis) + parseFloat(breathing) + parseFloat(tone) + parseFloat(altered);
    if (brue >= 1){ 
    var other = parseFloat(duration) + parseFloat(age) + parseFloat(prematurity) + parseFloat(prior) + parseFloat(cpr);
	msg = "BRUE";
	risk = "Not lower risk";
    rec = ". Proceed with usual workup.";
    }
    if (brue == 0) {
    msg = "Not BRUE"
    risk = ''
    rec = 'Proceed with usual workup'
    }

	if(other == 5){
		msg = "BRUE";
		risk = "Lower risk";
        rec = ". Assess social risk factors to detect child abuse. Should not admit for monitoring or do laboratory workup. See Advice for details."
	}
   if (other < 5) {
        msg = "BRUE"
        risk = 'Not lower risk'
        rec = ''
    }
}
//results
calc_output.push({
    name: 'mini',
    value: msg,
    value_text: '',
    message: risk
});
calc_output.push({
    name: 'Result',
    value: msg,
    value_text: '',
    message: risk + rec
});
