calc.equation_logic_text:
var calc_output = [];

//user inputs
var score = parseFloat(age) + parseFloat(sex) + parseFloat(cre) + parseFloat(ca) + parseFloat(cpk) + parseFloat(rhabdo) + parseFloat(phos) + parseFloat(bicarb);

var risk, msg

if (score <= 5.9) {
  risk = 'Low'
  msg = 'Recommend usual care (3% risk of death or AKI requiring RRT)'
}
else if (score >= 6) {
  risk = 'Not low'
  msg = 'Recommend initiating renal protective therapy. McMahon Scores ≥6 are 86% sensitive for identifying patients who will require RRT (52% risk of death or AKI requiring RRT at scores ≥10).'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Rhabdomyolysis Risk Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: ' risk',
    message: msg
});
