calc.equation_logic_text:
var calc_output = [];

//user inputs
var bastion = parseFloat(classification);
var s = parseFloat(s)
var a = parseFloat(a);
var b = parseFloat(b);
var c = parseFloat(c);
var d = parseFloat(d);

var suffix_s, suffix_a, suffix_b, suffix_c, suffix_d;

if(s == 1){
    suffix_s = 'S';
}
else {
    suffix_s = ''
}
if(a == 1){
    suffix_a = 'A';
}
else {
    suffix_a = ''
}
if(b == 1){
    suffix_b = 'B';
}
else {
    suffix_b = ''
}
if(c == 1){
    suffix_c = 'C';
}
else {
    suffix_c = ''
}
if(d == 1){
    suffix_d = 'D';
}
else {
    suffix_d = ''
}

var rec;
if (bastion == 1) {
    rec = 'Debridement ± plaster of paris splintage recommended. Vascular control: none required.'
    if (s == 1) {
        rec = 'Plaster of paris splintage ± debridement recommended. Vascular control: tourniquet, at most.'
    }
}
if (bastion == 2) {
    rec = 'Most likely low or high transtibial amputation needed. Vascular control: tourniquet at any level (1 patient had intraperitoneal iliac vessel control).'
    if (s == 1) {
        rec = 'Combination of debridement, PoP splintage, ex-fix recommended. Vascular control: tourniquet, at most.'
    }
}
if (bastion == 3) {
    rec = 'Most likely through-knee or low transfemoral amputation needed. Vascular control: tourniquet; may require intra- or less commonly extra-peritoneal vessel control'
    if (s == 1) {
        rec = 'Majority required debridement only; also included plaster of paris splintage, ex-fix, or low transfemoral amputation. Vascular control: tourniquet at any level; may require extra- or intra-peritoneal iliac vessel control'
    }
}
if (bastion == 4) {
    rec = 'Most likely high transfemoral amputation needed. Vascular control: intra-peritoneal iliac vessel control; less likely extra-peritoneal control.'
    if (s == 1) {
        rec = 'Majority required debridement only; also included high transtibial or transfemoral amputation. Vascular control: tourniquet, femoral vessel or intra-peritoneal iliac vessel control.'
    }
}
if (bastion == 5) {
    rec = 'Most likely high transfemoral amputation needed. Vascular control: intra-peritoneal iliac vessel control; less likely extra-peritoneal control or tourniquet.'
    if (s == 1) {
        rec = 'Debridement only recommended. Vascular control: extra-peritoneal vessel control.'
    }
}

//results
calc_output.push({
    name: 'mini',
    value: 'Class ' + bastion + suffix_s + ' ' + suffix_a + suffix_b + suffix_c + suffix_d,
    value_text: '',
    message: ''
});
calc_output.push({
    name: 'Bastion Classification',
    value: 'Class ' + bastion + suffix_s + ' ' + suffix_a + suffix_b + suffix_c + suffix_d,
    value_text: '',
    message: 'Bastion Classification'
});
calc_output.push({
    name: 'Rec',
    value: '',
    value_text: '',
    message: rec
});
