var calc_output = [];
var antibiotic, longterm, tube, pneumonia, hospitalization, pulmonary, status, gastric, wound, mrsa;

var u = parseFloat(antibiotic) + parseFloat(longterm) + parseFloat(tube) + parseFloat(pneumonia) + parseFloat(hospitalization) + parseFloat(pulmonary) + parseFloat(status) + parseFloat(gastric) + parseFloat(wound) + parseFloat(mrsa);

var risk, msg;

if (u < 4) {
    risk = 'Low'
    msg = 'Scores <4 were associated with lower risk of drug-resistant pneumonia. Consider treating without extended-spectrum antibiotics.'
}
if (u >= 4) {
    risk = 'High'
    msg = 'Scores ≥4 were associated with higher risk of drug-resistant pneumonia. Extended-spectrum antibiotics likely necessary.'
}

calc_output.push({ 
	name: 'mini', 
	value: u, 
	value_text: 'points', 
	message: risk 
});
calc_output.push({ 
	name: 'DRIP Score', 
	value: u, 
	value_text: 'points', 
	message: 'DRIP Score' 
});
calc_output.push({ 
	name: 'Risk', 
	value: risk, 
	value_text: 'risk', 
	message: msg 
});
