calc.equation_logic_text:
var calc_output = [];

var risk; 
var survival;

var age = parseFloat(age);
var stage = parseFloat(stage);
var microglobulin = parseFloat(microglobulin);
var mutational = parseFloat(mutational);
var tp53 = parseFloat(tp53);

var score = age + stage + microglobulin + mutational + tp53;

if (score == 0 || score == 1 ) {
  risk = 'Low';
  survival = '93.2% 5 year survival';
}
if (score == 2 || score == 3 ) {
  risk = 'Intermediate';
  survival = '79.3% 5 year survival';
}
if (score == 4 || score == 5 || score == 6) {
  risk = 'High';
  survival = '63.3% 5 year survival';
}
if (score == 7 || score == 8 || score == 9 || score == 10) {
  risk = 'Very high';
  survival = '23.3% 5 year survival';
}

calc_output.push({
  name: 'mini',
  value: score.toFixed(0),
  value_text: 'points',
  message: risk + ' risk'
});
calc_output.push({
  name: 'CLL-IPI score',
  value: score.toFixed(0),
  value_text: 'points',
  message: 'CLL-IPI'
});
calc_output.push({
  name: 'Risk message',
  value: risk,
  value_text: 'risk',
  message: survival
});
