       var apparent = parseFloat(apparent);
       var reported = parseFloat(reported);
       var tension = parseFloat(tension);
       var sleep = parseFloat(sleep);
       var appetite = parseFloat(appetite);
       var concentration = parseFloat(concentration);
       var lassitude = parseFloat(lassi);
       var feel = parseFloat(feel);
       var pessimistic = parseFloat(pessimistic);
       var suicidal = parseFloat(suicidal);

       var score = apparent + reported + tension + sleep + appetite + concentration + lassitude + feel + pessimistic + suicidal;
       var msg;

       if (score >= 0 && score <= 8) {
           msg = 'Depressive symptoms absent';
       }
       if (score >= 9 && score <= 17) {
           msg = 'Mild depression';
       }
       if (score >= 18 && score <= 34) {
           msg = 'Moderate depression';
       }
       if (score >= 35 && score <= 60) {
           msg = 'Severe depression';
       }

calc_output.push({
   name: 'mini',
   value: score,
   value_text: 'points',
