calc.equation_logic_text:
var calc_output = [];

var height = parseFloat(height)
var weight = parseFloat(weight)
var hct = parseFloat(hct)
var sex = parseFloat(sex)
var hct_donor = parseFloat(hct_donor)
var hgbs_initial = parseFloat(hgbs_initial)
var hgbs_goal = parseFloat(hgbs_goal)

var tbv

if (sex	== 0) { // male
	tbv = 0.3669 * Math.pow(height / 100, 3) + 0.03219 * weight + 0.6041
}
else {
	tbv = 0.3561 * Math.pow(height / 100, 3) + 0.03308 * weight + 0.1833
}

var rbc = tbv * 1000 * hct 

var rbc_exchange = rbc * Math.log(hgbs_initial/hgbs_goal) / hct_donor

var units = rbc_exchange / 350

//results
calc_output.push({
    name: 'mini',
    value: rbc_exchange.toFixed(0),
    value_text: 'mL',
    message: 'RBC exchange'
});
calc_output.push({
    name: 'RBC exchange',
    value: rbc_exchange.toFixed(0),
    value_text: 'mL',
    message: 'RBC exchange replacement volume'
});
calc_output.push({
    name: 'units',
    value: units.toFixed(0),
    value_text: 'units',
    message: 'Approximate number of units, assuming 350 mL RBCs per unit'
});
