var ONE_DAY = 1000 * 60 * 60 * 24;

function wksndays(date1, date2) {
    var date1_ms = date1.getTime();
    var date2_ms = date2.getTime();
    var difference_ms = date1_ms - date2_ms;
    var days = Math.floor(difference_ms / ONE_DAY);
    var weeks = Math.floor(days / 7);
    var remainder = days - 7 * weeks;
    return [weeks, remainder, days];
}

function formatDate(date) {
    var d = new Date(date.getTime());
    return weekday[d.getUTCDay()] + ", " + (months[d.getUTCMonth()]) + " " + d.getUTCDate() + ", " + d.getUTCFullYear();
}

