calc.equation_logic_text:
var calc_output = [];

var systolic_bp = parseFloat(systolic_bp);
var sex = parseFloat(sex);
var bun, bun_input, hgb, sbp, u, hemoglobin;
var msg = '';
var mini_msg = '';
if (UOMSYSTEM == false) {
  hemoglobin = parseFloat(hemoglobin) / 10;
  bun_input = parseFloat(bun) / 0.357;
}

if (UOMSYSTEM == true) {
  hemoglobin = parseFloat(hemoglobin);
  bun_input = parseFloat(bun);
}

if (bun_input < 18.2) {
  bun = 0;
}
if (bun_input >= 18.2 && bun_input < 22.4) {
  bun = 2;
}
if (bun_input >= 22.4 && bun_input < 28.0) {
  bun = 3;
}
if (bun_input >= 28.0 && bun_input <= 70.0) {
  bun = 4;
}
if (bun_input > 70.0) {
  bun = 6;
}
if (sex == 1) {
  if (hemoglobin >= 12) {
    hgb = 0;
  }
  if ((hemoglobin < 12) && (hemoglobin >= 10)) {
    hgb = 1;
  }
  if (hemoglobin < 10) {
    hgb = 6;
  }
}
if (sex == 0) {
  if (hemoglobin >= 13) {
    hgb = 0;
  }
  if ((hemoglobin < 13) && (hemoglobin >= 12)) {
    hgb = 1;
  }
  if ((hemoglobin < 12) && (hemoglobin >= 10)) {
    hgb = 3;
  }
  if (hemoglobin < 10) {
    hgb = 6;
  }
}
if (systolic_bp >= 109) {
  sbp = 0;
}
if ((systolic_bp <= 109) && (systolic_bp >= 100)) {
  sbp = 1;
}
if ((systolic_bp < 100) && (systolic_bp >= 90)) {
  sbp = 2;
}
if (systolic_bp < 90) {
  sbp = 3;
};
u = hgb + bun + sbp + parseFloat(heart_rate_100) + parseFloat(melena) +
  parseFloat(syncope) + parseFloat(hepatic_disease) + parseFloat(heart_failure);
if (u == 0) {
    msg = 'A GBS of 0 is a \u201cLow Risk\u201d GI bleed, and is highly sensitive (99.6% in a 2007 retrospective study) for predicting which patients did not require any \u201cmedical intervention\u201d: blood transfusion, endoscopy, or surgery. This was confirmed in a 2009 Lancet study where patients with a score of 0 were actually discharged and had no GI bleeding mortality at 6 month followup';
    mini_msg = 'Low Risk for GI Bleed';
}
if (u > 0) {
    msg = 'A GBS greater than zero suggests a “High Risk” GI bleed that is likely to require “medical intervention”: transfusion, endoscopy, or surgery. A higher GBS also correlated with a higher likelihood of needing intervention (scores ≥6 are associated with >50% risk of needing intervention)';
    mini_msg = 'High Risk for GI bleed';
}
calc_output.push({
  name: 'mini',
  value: u.toFixed(0),
  value_text: 'points',
  message: mini_msg
});
calc_output.push({
  name: '518_GBS Score_result',
  value: u.toFixed(0),
  value_text: 'points',
  message: msg
});
