calc.equation_logic_text:
var calc_output=[]; 

var msg=''; 
var time=1; 

if(ischemia_6_hours==1){time=2};

var u=(time*parseInt(limb_ischemia))+parseInt(age)+parseInt(shock)+parseInt(injury); 

if(u<7){msg='Per original study, scores ≤6 were predictive of salvaged limb. Subsequent validations found multiple instances of amputation in patients with MESS ≤6.'} 
if(u>6){msg='Scores ≥7 predict a low likelihood of limb/extremity viability (100% sensitive in validation).'} 

calc_output.push({
    name:'mini',
    value:u,
    value_text:'points',
    message:'MESS ≥7 predicts low likelihood of limb/extremity viability.'
}); 
calc_output.push({
    name:'549_MESS_result',
    value:u,
    value_text:'points',
    message:msg
});
