calc.equation_logic_text:
var calc_output = []; 

var eye = parseInt(eye)
var motor = parseInt(motor)
var verbal = parseInt(verbal)

// SCORE (total points)
var score = eye + motor + verbal

// SCALE (individual components)
var eye_scale, verbal_scale, motor_scale

eye == 0 ? eye_scale = 'NT' : eye_scale = eye

verbal == 0 ? verbal_scale = 'NT' : verbal_scale = verbal

motor == 0 ? motor_scale = 'NT' : motor_scale = motor

var scale = 'E(' + eye_scale + ') ' + 'V(' + verbal_scale + ') ' + 'M(' + motor_scale + ') ' 


if (eye == 0 || verbal == 0 || motor == 0) {
  calc_output.push({ 
    name: 'mini', 
    value: scale, 
    value_text: '', 
    message: 'Glasgow Coma Scale' 
  }); 
  calc_output.push({ 
    name: '64_GCS_result (score)', 
    value: '', 
    value_text: '', 
    message: 'Glasgow Coma Score cannot be calculated (one or more components not testable)' 
  });
}
else {
  calc_output.push({ 
    name: 'mini', 
    value: score, 
    value_text: 'points', 
    message: 'Glasgow Coma Score' 
  }); 
  calc_output.push({ 
    name: '64_GCS_result (score)', 
    value: score, 
    value_text: 'points', 
    message: 'Glasgow Coma Score' 
  });
}
  calc_output.push({ 
    name: '64_GCS_result (scale)', 
    value: scale,
    value_text: '', 
    message: 'Glasgow Coma Scale'
  });
