calc.equation_logic_text:
var calc_output = [];
var plt = parseFloat(plt);
var gcs = parseFloat(gcs);
var bili = parseFloat(bili);
var po2 = parseFloat(po2);
var fio2 = parseFloat(fio2)/100;
var map = parseFloat(map);
var cr = parseFloat(cr);
var mv = parseFloat(mv);

if (UOMSYSTEM == false) {
    po2 = po2/0.133;
}
else {
	po2 = po2;
}

var pf_ratio = po2 / fio2;

if (pf_ratio >= 400) {
  pf_ratio = 0;
} else if(pf_ratio >= 300 && pf_ratio < 400) {
  pf_ratio = 1;
} else if (pf_ratio >= 200 && pf_ratio < 300){
  pf_ratio = 2;
} else if (pf_ratio >= 100 && pf_ratio < 200 && mv == 1){
  pf_ratio = 3;
} else if (pf_ratio >= 100 && pf_ratio < 200 && mv == 0){
  pf_ratio = 2;
} else if (pf_ratio < 100 && mv == 1){
  pf_ratio = 4;
} else if (pf_ratio < 100 && mv == 0){
  pf_ratio = 2;
}

u = pf_ratio + plt + gcs + bili + map + cr;

var msg, mini_msg

if (u <= 9) {
    msg = 'Initial SOFA Scores ≤9 predict ≤33.3% mortality (see Evidence for details)';
    mini_msg = 'Mortality <33%'
}
if (u > 9 && u < 12) {
    msg = 'Initial SOFA Scores of 10-11 predict 50.0% mortality (see Evidence for details)';
    mini_msg = 'Mortality 50%'
}
if (u >= 12 ) {
    msg = 'Initial SOFA Scores ≥12 predict ≥95.2% mortality (see Evidence for details)';
    mini_msg = 'Mortality >95%'
}
calc_output.push({ 
  name: 'mini', 
  value: u.toFixed(0), 
  value_text: 'points', 
  message: mini_msg 
});
calc_output.push({ 
  name: '691_SOFA Score_result', 
  value: u.toFixed(0), 
  value_text: 'points', 
  message: msg 
});
