calc.equation_logic_text:
var calc_output = [];

var inr = (parseFloat(inr) < 1) ? 1 : parseFloat(inr);
var na = parseFloat(na);

if (UOMSYSTEM == true) {
  var bili = parseFloat(bilirubin);
  var cr = parseFloat(creatinine);

  if (bili < 1) {
    bili = 1;
  }
  if (cr < 1) {
    cr = 1;
  }

}

if (UOMSYSTEM == false) {
  var bili = parseFloat(bilirubin) / 17.104;
  var cr = parseFloat(creatinine) / 88.4;

  // these are now converted to US units, so now do a check to make sure they're not <1, otherwise set to 1

  if (bili < 1) {
    bili = 1;
  }
  if (cr < 1) {
    cr = 1;
  }

}

// correct for extremes of na
if (na < 125) {
  na = 125
}
else if (na > 137) {
  na = 137
}
else {
  na = na
}

// correct for dialysis or high cr
if (dialysis == 1 || cr > 4) {
  cr = 4
}
else {
  cr = cr
}
// var meld_i = 10 * ((.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + (.957 *
//   Math.log(cr)) + .643);
var meld_i = (.957 * Math.log(cr)) + (.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + .643;
meld_i = 10 * meld_i.toFixed(1)


var meldnew;

if (meld_i <= 11) {
    if (meld_i < 6) {
    meldnew = 6;
    }
    else {
    meldnew = meld_i
  }
}
if (meld_i > 11) {
  if (meld_i >= 40) {
  var meldnew = 40
  }
  else {
  var nadiff = 137 - na;
  meldnew = meld_i + (1.32 * nadiff) - (0.033 * meld_i * nadiff);
  }
} 

if (meldnew >= 40) {
  meldnew = 40
}

var mort = '';
if (meldnew.toFixed(0) <= 9) {
  mort = '1.9%';
}
else if (meldnew.toFixed(0) >= 10 && meldnew.toFixed(0) < 20) {
  mort = '6.0%';
}
else if (meldnew.toFixed(0) >= 20 && meldnew.toFixed(0) < 30) {
  mort = '19.6%';
}
else if (meldnew.toFixed(0) >= 30 && meldnew.toFixed(0) < 40) {
  mort = '52.6%';
}
else if (meldnew.toFixed(0) >= 40) {
  mort = '71.3%';
}



calc_output.push({
  name: 'mini',
  value: meldnew.toFixed(0),
  value_text: 'points',
  message: 'MELD Score (2016)*'
});
calc_output.push({
  name: '78_MELD Na (UNOS/OPTN)_result (score)',
  value: meldnew.toFixed(0),
  value_text: 'points',
  message: 'MELD Score (2016)*'
});
calc_output.push({
  name: '78_MELD Na (UNOS/OPTN)_result (mortality)',
  value: mort,
  value_text: ' ',
  message: 'Estimated 3-Month Mortality'
});
